package operator;

public class Ope {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int length=40;
System.out.println("output is"+length);
System.out.println(length+=20);
System.out.println(length-=5);
System.out.println(length*=2);
System.out.println(length/=10);
System.out.println(length>50?true:false);//conditional operator
int a=15;
int b=36;
System.out.println(a>10 && b<50);
System.out.println(a<10 || b>50);

	}

}
