package test;

public class Ifloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
  // Find biggest of two numbers using if else loop
		
		int a=234;
		int b=475;
		if(a>b)
		{
			System.out.println("Biggest number is:"+a);
		}
		else
		{
			System.out.println("Biggest number is:"+b);
		}
	}

}
